import { StyleSheet, Platform } from 'react-native';

const styles = StyleSheet.create({
  conteiner: {
        height: Platform.OS === "web" ? "100vh" : "100%",
        backgroundColor: '#292929',
        //web 100vh
    },
    conteudo: {
        width: "100%",
        height: "100%",
        justifyContent: 'center',
        alignItems: 'center'
    },
    titulo: {
        color: "white",
        fontSize: 32
    },
    inputs: {
        marginHorizontal: 30,
        marginTop: 10,
        alignItems: 'center',
    },
    input: {
        backgroundColor: "white",
        width: "100%",
        padding: 8,
        marginTop: 12
    },
    btn:{
        height: 50,
        margin: 15,
        alignItems: "center",
        borderRadius: 30,
        justifyContent: "center",
        backgroundColor: "#000000",
    },
    texto:{
        fontSize: 18,
        color: "white",
        textAlign: 'center'
    }
})

export default styles;
